import { REGISTER_USER, LOAD_USER, FAIL_USER, LOGOUT_USER, CURRENT_USER } from '../ActionTypes/user';

const initialState = {
  user: null, // Current user data
  isAuth: false, // Authentication status
  isLoading: true, // Loading state
  errors: [], // Errors related to user actions
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_USER:
      return {
        ...state,
        isLoading: false,
        user: action.payload.user,
        isAuth: true,
        errors: [],
      };
    case LOAD_USER:
      return {
        ...state,
        isLoading: true,
        errors: [],
      };
    case CURRENT_USER:
      return {
        ...state,
        isLoading: false,
        user: action.payload.user,
        isAuth: true,
        errors: [],
      };
    case FAIL_USER:
      return {
        ...state,
        isLoading: false,
        isAuth: false,
        errors: action.payload,
      };
    case LOGOUT_USER:
      return initialState; // Reset the state on logout
    default:
      return state;
  }
};

export default userReducer;
